mapper.select:

            select bno, title, content, id, regDate, updateDate, viewCount, replyCount
			from (
				select 
					rownum rn, bno, title, content, id, regDate, updateDate, viewCount, replyCount
					from board
					where 1 = 1
					and bno > 0






            and rownum <= #{pageNum} * #{amount}
					order by bno desc
				)
			where 1 = 1
			and rn > (#{pageNum} - 1) * #{amount}

mapper.update:



update board set
title = #{title},
content = #{content},
updateDate = #{updateDate}
where 1 = 1
and bno = #{bno}

mapper.delete:

delete from board
where 1 = 1
and bno = #{bno}

mapper.update:

update board set
replyCount = replyCount + #{count}
where 1 = 1
and bno = #{bno}
