mapper.select:

            select 
            /*+ index_asc(item SYS_C007008) */
            ino, category, name, price, amount, discount, content
			from (
				select 
					/*+ index_asc(item SYS_C007008) */
					rownum rn, ino, category, name, price, amount, discount, content
					from item
					where 1 = 1
					and ino > 0







						and rownum <= #{pageNum} * #{amount}



		        		order by price



		        		order by price desc



		        		order by regdate desc


				 )
			where 1 = 1
			and rn > (#{pageNum} - 1) * #{amount}

mapper.delete:

delete from item
where 1 = 1
and ino = #{ino}
